import { useState, useEffect } from "react";
import Rupiah from "../components/Rupiah";

const OrderList = () => {
  const [allOrders, setAllOrders] = useState([]);
  const [expandedOrders, setExpandedOrders] = useState(new Set());
  const [isLoading, setIsLoading] = useState(true);

  const API_BASE_URL = "http://localhost:6543/api";

  // Order status configuration
  const orderStatuses = {
    'pending': { 
      label: 'Pending', 
      color: 'bg-yellow-100 text-yellow-800',
      dotColor: 'bg-yellow-400'
    },
    'confirmed': { 
      label: 'Confirmed', 
      color: 'bg-blue-100 text-blue-800',
      dotColor: 'bg-blue-400'
    },
    'preparing': { 
      label: 'Preparing', 
      color: 'bg-orange-100 text-orange-800',
      dotColor: 'bg-orange-400'
    },
    'shipping': { 
      label: 'Shipping', 
      color: 'bg-purple-100 text-purple-800',
      dotColor: 'bg-purple-400'
    },
    'delivered': { 
      label: 'Delivered', 
      color: 'bg-green-100 text-green-800',
      dotColor: 'bg-green-400'
    },
    'completed': { 
      label: 'Completed', 
      color: 'bg-green-100 text-green-800',
      dotColor: 'bg-green-400'
    },
    'cancelled': { 
      label: 'Cancelled', 
      color: 'bg-red-100 text-red-800',
      dotColor: 'bg-red-400'
    }
  };

  useEffect(() => {
    const fetchAllOrders = async () => {
      try {
        const response = await fetch(`${API_BASE_URL}/orders`, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
          credentials: 'include'
        });
        const data = await response.json();
        setAllOrders(data.orders || []);
      } catch (err) {
        console.error("Failed to load orders:", err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchAllOrders();
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'numeric',
      day: 'numeric'
    });
  };

  const toggleOrderExpansion = (orderId) => {
    const newExpanded = new Set(expandedOrders);
    if (newExpanded.has(orderId)) {
      newExpanded.delete(orderId);
    } else {
      newExpanded.add(orderId);
    }
    setExpandedOrders(newExpanded);
  };

  const truncateOrderId = (orderId) => {
    return orderId.length > 8 ? `${orderId.substring(0, 8)}...` : orderId;
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="bg-white rounded-lg shadow-sm p-8 text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-4 text-gray-600">Loading orders...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Your Orders</h1>
          <p className="mt-2 text-gray-600">Track and manage your orders</p>
        </div>

        <div className="bg-white rounded-lg shadow-sm overflow-hidden">
          {/* Table Header */}
          <div className="bg-gray-50 px-6 py-4 border-b border-gray-200">
            <div className="grid grid-cols-12 gap-4 text-sm font-medium text-gray-700">
              <div className="col-span-3">Order ID</div>
              <div className="col-span-3">Customer</div>
              <div className="col-span-2">Date</div>
              <div className="col-span-2">Total</div>
              <div className="col-span-2">Status</div>
            </div>
          </div>

          {/* Orders List */}
          <div className="divide-y divide-gray-200">
            {allOrders.length === 0 ? (
              <div className="px-6 py-12 text-center">
                <div className="text-gray-400 text-lg mb-2">ðŸ“¦</div>
                <p className="text-gray-500">No orders found</p>
              </div>
            ) : (
              allOrders.map((order) => (
                <div key={order.orderId} className="transition-all duration-200">
                  {/* Main Order Row */}
                  <div 
                    className="px-6 py-4 hover:bg-gray-50 cursor-pointer transition-colors duration-150"
                    onClick={() => toggleOrderExpansion(order.orderId)}
                  >
                    <div className="grid grid-cols-12 gap-4 items-center">
                      {/* Order ID */}
                      <div className="col-span-3">
                        <div className="flex items-center">
                          <span className="text-sm font-medium text-gray-900">
                            {truncateOrderId(order.orderId)}
                          </span>
                          <svg 
                            className={`ml-2 h-4 w-4 text-gray-400 transition-transform duration-200 ${
                              expandedOrders.has(order.orderId) ? 'transform rotate-180' : ''
                            }`}
                            fill="none" 
                            viewBox="0 0 24 24" 
                            stroke="currentColor"
                          >
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                          </svg>
                        </div>
                      </div>

                      {/* Customer */}
                      <div className="col-span-3">
                        <div>
                          <p className="text-sm font-medium text-gray-900">
                            {order.customerName || 'Unknown'}
                          </p>
                          <p className="text-sm text-gray-500">
                            {order.customerEmail || 'No email'}
                          </p>
                        </div>
                      </div>

                      {/* Date */}
                      <div className="col-span-2">
                        <span className="text-sm text-gray-900">
                          {formatDate(order.orderDate)}
                        </span>
                      </div>

                      {/* Total */}
                      <div className="col-span-2">
                        <span className="text-sm font-medium text-gray-900">
                          <Rupiah value={order.total} />
                        </span>
                      </div>

                      {/* Status */}
                      <div className="col-span-2">
                        <span 
                          className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
                            orderStatuses[order.status]?.color || orderStatuses['pending'].color
                          }`}
                        >
                          <span 
                            className={`w-1.5 h-1.5 rounded-full mr-1.5 ${
                              orderStatuses[order.status]?.dotColor || orderStatuses['pending'].dotColor
                            }`}
                          />
                          {orderStatuses[order.status]?.label || 'Unknown'}
                        </span>
                      </div>
                    </div>
                  </div>

                  {/* Expanded Details */}
                  {expandedOrders.has(order.orderId) && (
                    <div className="px-6 pb-6 bg-gray-50 border-t border-gray-100">
                      <div className="mt-4 space-y-4">
                        {/* Full Order ID */}
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                          <div>
                            <p className="text-xs font-medium text-gray-500 uppercase tracking-wide">
                              Full Order ID
                            </p>
                            <p className="mt-1 text-sm text-gray-900 font-mono">
                              {order.orderId}
                            </p>
                          </div>
                          <div>
                            <p className="text-xs font-medium text-gray-500 uppercase tracking-wide">
                              Order Date & Time
                            </p>
                            <p className="mt-1 text-sm text-gray-900">
                              {new Date(order.orderDate).toLocaleString('en-US', {
                                year: 'numeric',
                                month: 'short',
                                day: 'numeric',
                                hour: '2-digit',
                                minute: '2-digit'
                              })}
                            </p>
                          </div>
                        </div>

                        {/* Order Items */}
                        <div>
                          <p className="text-xs font-medium text-gray-500 uppercase tracking-wide mb-3">
                            Items Ordered ({order.items?.length || 0})
                          </p>
                          <div className="space-y-3">
                            {order.items?.map((item, index) => (
                              <div key={index} className="flex items-center space-x-4 p-3 bg-white rounded-lg border border-gray-200">
                                <img 
                                  src={item.product?.image || '/placeholder-image.jpg'} 
                                  alt={item.product?.title || 'Product'} 
                                  className="h-12 w-12 object-contain rounded border bg-gray-50"
                                />
                                <div className="flex-1 min-w-0">
                                  <h4 className="text-sm font-medium text-gray-900 truncate">
                                    {item.product?.title || 'Unknown Product'}
                                  </h4>
                                  <p className="text-sm text-gray-500">
                                    Qty: {item.quantity} Ã— <Rupiah value={item.price} />
                                  </p>
                                </div>
                                <div className="text-sm font-medium text-gray-900">
                                  <Rupiah value={((item.price || 0) * (item.quantity || 1))} />
                                </div>
                              </div>
                            )) || (
                              <p className="text-sm text-gray-500 italic">No items found</p>
                            )}
                          </div>
                        </div>

                        {/* Status History */}
                        {order.statusHistory && order.statusHistory.length > 0 && (
                          <div>
                            <p className="text-xs font-medium text-gray-500 uppercase tracking-wide mb-3">
                              Status History
                            </p>
                            <div className="space-y-2">
                              {order.statusHistory.slice().reverse().map((history, index) => (
                                <div key={index} className="flex items-start space-x-3 p-3 bg-white rounded-lg border border-gray-200">
                                  <div 
                                    className={`w-6 h-6 rounded-full flex items-center justify-center text-xs ${
                                      orderStatuses[history.status]?.color || orderStatuses['pending'].color
                                    }`}
                                  >
                                    <span 
                                      className={`w-2 h-2 rounded-full ${
                                        orderStatuses[history.status]?.dotColor || orderStatuses['pending'].dotColor
                                      }`}
                                    />
                                  </div>
                                  <div className="flex-1 min-w-0">
                                    <div className="flex justify-between items-start">
                                      <div>
                                        <p className="text-sm font-medium text-gray-900">
                                          {orderStatuses[history.status]?.label || history.status}
                                        </p>
                                        {history.note && (
                                          <p className="text-sm text-gray-600 mt-1">
                                            {history.note}
                                          </p>
                                        )}
                                      </div>
                                      <div className="text-right">
                                        <div className="text-xs text-gray-500">
                                          {new Date(history.timestamp).toLocaleDateString()}
                                        </div>
                                        {history.updatedBy && (
                                          <div className="text-xs text-gray-400 mt-1">
                                            by {history.updatedBy}
                                          </div>
                                        )}
                                      </div>
                                    </div>
                                  </div>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}

                        {/* Order Summary */}
                        <div className="pt-4 border-t border-gray-200">
                          <div className="flex justify-between items-center">
                            <span className="text-sm font-medium text-gray-500">Order Total:</span>
                            <span className="text-lg font-bold text-blue-600">
                              <Rupiah value={order.total} />
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderList;
