import { useState, useEffect } from "react";

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [trackingId, setTrackingId] = useState("");
  const [trackedOrder, setTrackedOrder] = useState(null);
  const [showTrackingResult, setShowTrackingResult] = useState(false);

  // Order status configuration
  const orderStatuses = {
    'pending': { 
      label: 'Pending', 
      color: 'bg-yellow-100 text-yellow-800',
      step: 0
    },
    'processing': { 
      label: 'Processing', 
      color: 'bg-blue-100 text-blue-800',
      step: 1
    },
    'shipped': { 
      label: 'Shipped', 
      color: 'bg-purple-100 text-purple-800',
      step: 2
    },
    'delivered': { 
      label: 'Delivered', 
      color: 'bg-green-100 text-green-800',
      step: 3
    },
    'cancelled': { 
      label: 'Cancelled', 
      color: 'bg-red-100 text-red-800',
      step: -1
    }
  };

  const trackingSteps = [
    { key: 'pending', label: 'Order Placed' },
    { key: 'processing', label: 'Processing' },
    { key: 'shipped', label: 'Shipped' },
    { key: 'delivered', label: 'Delivered' }
  ];

  useEffect(() => {
    // Sample orders data
    const sampleOrders = [
      {
        orderId: '81d25430-3192-4106-a231-7a12cb0214a7',
        orderDate: '5/23/2025',
        status: 'pending',
        total: '$449.98',
        customerInfo: {
          fullName: 'John Doe',
          email: 'john@example.com',
          address: 'Jl. Sudirman No. 123, Jakarta',
          phoneNumber: '+62 812-3456-7890'
        },
        items: [
          {
            title: 'Smart Watch',
            price: 199.99,
            quantity: 1,
            image: 'https://via.placeholder.com/80/CCCCCC/000000?text=Watch'
          },
          {
            title: 'Stylish Headphones',
            price: 249.99,
            quantity: 1,
            image: 'https://via.placeholder.com/80/FFD700/000000?text=Headphones'
          }
        ]
      },
      {
        orderId: 'ORD-12345-67890',
        orderDate: '5/22/2025',
        status: 'shipped',
        total: '$299.99',
        customerInfo: {
          fullName: 'Jane Smith',
          email: 'jane@example.com',
          address: 'Jl. Thamrin No. 456, Jakarta',
          phoneNumber: '+62 813-7890-1234'
        },
        items: [
          {
            title: 'Wireless Earbuds',
            price: 299.99,
            quantity: 1,
            image: 'https://via.placeholder.com/80/000080/FFFFFF?text=Earbuds'
          }
        ]
      },
      {
        orderId: 'ORD-98765-43210',
        orderDate: '5/21/2025',
        status: 'delivered',
        total: '$599.99',
        customerInfo: {
          fullName: 'Bob Johnson',
          email: 'bob@example.com',
          address: 'Jl. Gatot Subroto No. 789, Jakarta',
          phoneNumber: '+62 814-5678-9012'
        },
        items: [
          {
            title: 'Gaming Laptop',
            price: 599.99,
            quantity: 1,
            image: 'https://via.placeholder.com/80/FF0000/FFFFFF?text=Laptop'
          }
        ]
      }
    ];

    // Try to get orders from localStorage, fallback to sample data
    const storedOrders = JSON.parse(localStorage.getItem("orders") || "[]");
    const ordersToShow = storedOrders.length > 0 ? storedOrders : sampleOrders;
    
    setOrders(ordersToShow);
  }, []);

  const handleTrackOrder = () => {
    if (!trackingId.trim()) return;
    
    const foundOrder = orders.find(order => 
      order.orderId.toLowerCase() === trackingId.toLowerCase().trim()
    );
    
    if (foundOrder) {
      setTrackedOrder(foundOrder);
      setShowTrackingResult(true);
    } else {
      setTrackedOrder(null);
      setShowTrackingResult(true);
    }
  };

  const getCurrentStep = (status) => {
    return orderStatuses[status]?.step || 0;
  };

  const TrackingProgress = ({ currentStatus }) => {
    const currentStep = getCurrentStep(currentStatus);
    
    if (currentStatus === 'cancelled') {
      return (
        <div className="text-center py-4">
          <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-red-100 text-red-800">
            Order Cancelled
          </span>
        </div>
      );
    }

    return (
      <div className="relative">
        <div className="flex items-center justify-between mb-8">
          {trackingSteps.map((step, index) => {
            const isActive = index <= currentStep;
            
            return (
              <div key={step.key} className="flex flex-col items-center relative flex-1">
                <div className={`w-12 h-12 rounded-full flex items-center justify-center text-sm font-semibold ${
                  isActive 
                    ? 'bg-purple-500 text-white' 
                    : 'bg-gray-200 text-gray-400'
                }`}>
                  {isActive ? 'âœ“' : index + 1}
                </div>
                <span className={`text-sm mt-2 text-center ${
                  isActive ? 'text-purple-600 font-medium' : 'text-gray-400'
                }`}>
                  {step.label}
                </span>
              </div>
            );
          })}
        </div>
        
        {/* Progress Line */}
        <div className="absolute top-6 left-0 right-0 h-1 bg-gray-200" style={{ zIndex: -1 }}>
          <div 
            className="h-full bg-purple-500 transition-all duration-300 ease-in-out" 
            style={{ width: `${(currentStep / (trackingSteps.length - 1)) * 100}%` }}
          />
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-purple-500 text-white py-4">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-white bg-opacity-20 rounded flex items-center justify-center">
                <span className="text-sm">ðŸ“¦</span>
              </div>
              <span className="text-xl font-bold">MachzaulMart</span>
            </div>
            <nav className="hidden md:flex space-x-6">
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">Products</button>
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">About</button>
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">Contact</button>
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">Track Order</button>
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">Admin</button>
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">Orders</button>
              <button className="hover:text-purple-200 bg-transparent border-none cursor-pointer">ðŸ›’</button>
            </nav>
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Track Order Section */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-8">Track Your Order</h1>
          
          <div className="flex justify-center space-x-4 mb-8">
            <input
              type="text"
              placeholder="Enter your order ID"
              value={trackingId}
              onChange={(e) => setTrackingId(e.target.value)}
              className="flex-1 max-w-md px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
              onKeyPress={(e) => e.key === 'Enter' && handleTrackOrder()}
            />
            <button
              onClick={handleTrackOrder}
              className="px-6 py-3 bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition-colors font-medium"
            >
              Track Order
            </button>
          </div>
        </div>

        {/* Tracking Result */}
        {showTrackingResult && (
          <div className="bg-white rounded-lg shadow-lg p-6 mb-8">
            {trackedOrder ? (
              <div>
                {/* Order Info */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Order ID</h3>
                    <p className="text-lg font-semibold text-gray-900">{trackedOrder.orderId}</p>
                  </div>
                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Order Date</h3>
                    <p className="text-lg font-semibold text-gray-900">{trackedOrder.orderDate}</p>
                  </div>
                  <div>
                    <h3 className="text-sm font-medium text-gray-500">Total</h3>
                    <p className="text-lg font-semibold text-gray-900">{trackedOrder.total}</p>
                  </div>
                </div>

                {/* Order Status */}
                <div className="mb-8">
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">
                    Order Status: <span className="text-purple-600">{orderStatuses[trackedOrder.status]?.label || 'Unknown'}</span>
                  </h3>
                  <TrackingProgress currentStatus={trackedOrder.status} />
                </div>

                {/* Ordered Items */}
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-4">Ordered Items</h3>
                  <div className="space-y-4">
                    {trackedOrder.items.map((item, index) => (
                      <div key={index} className="flex items-center space-x-4 p-4 bg-gray-50 rounded-lg">
                        <img 
                          src={item.image} 
                          alt={item.title}
                          className="w-16 h-16 object-contain rounded border bg-white"
                        />
                        <div className="flex-1">
                          <h4 className="font-medium text-gray-900">{item.title}</h4>
                          <p className="text-sm text-gray-600">Qty: {item.quantity}</p>
                        </div>
                        <div className="text-lg font-semibold text-gray-900">
                          ${item.price}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ) : (
              <div className="text-center py-8">
                <div className="text-red-500 mb-4">
                  <svg className="mx-auto h-16 w-16" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L4.082 18.5c-.77.833.192 2.5 1.732 2.5z" />
                  </svg>
                </div>
                <h3 className="text-lg font-medium text-gray-900">Order Not Found</h3>
                <p className="text-gray-500 mt-2">Please check your order ID and try again.</p>
              </div>
            )}
          </div>
        )}

        {/* All Orders List */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">All Orders</h2>
          
          {orders.length === 0 ? (
            <div className="text-center py-12">
              <svg className="mx-auto h-24 w-24 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
              </svg>
              <h3 className="mt-4 text-lg font-medium text-gray-900">No orders yet</h3>
              <p className="mt-2 text-gray-500">You haven't placed any orders yet. Start shopping to see your orders here!</p>
            </div>
          ) : (
            <div className="space-y-6">
              {orders.map((order, index) => {
                const statusInfo = orderStatuses[order.status] || orderStatuses['pending'];
                
                return (
                  <div key={index} className="border border-gray-200 rounded-lg p-6 hover:shadow-md transition-shadow">
                    {/* Order Header */}
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-4">
                      <div>
                        <h3 className="text-lg font-semibold text-gray-900">
                          Order #{order.orderId}
                        </h3>
                        <p className="text-sm text-gray-600">
                          Placed on {order.orderDate}
                        </p>
                      </div>
                      <div className="mt-2 sm:mt-0 flex items-center space-x-4">
                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${statusInfo.color}`}>
                          {statusInfo.label}
                        </span>
                        <span className="text-lg font-bold text-gray-900">{order.total}</span>
                      </div>
                    </div>

                    {/* Order Items Summary */}
                    <div className="flex items-center space-x-4">
                      <div className="flex -space-x-2">
                        {order.items.slice(0, 3).map((item, itemIndex) => (
                          <img 
                            key={itemIndex}
                            src={item.image} 
                            alt={item.title}
                            className="w-10 h-10 rounded-full border-2 border-white object-contain bg-gray-100"
                          />
                        ))}
                        {order.items.length > 3 && (
                          <div className="w-10 h-10 rounded-full border-2 border-white bg-gray-100 flex items-center justify-center text-xs font-medium text-gray-600">
                            +{order.items.length - 3}
                          </div>
                        )}
                      </div>
                      <div className="flex-1">
                        <p className="text-sm text-gray-600">
                          {order.items.length} item{order.items.length > 1 ? 's' : ''}
                        </p>
                      </div>
                      <button
                        onClick={() => {
                          setTrackingId(order.orderId);
                          setTrackedOrder(order);
                          setShowTrackingResult(true);
                        }}
                        className="text-purple-600 hover:text-purple-800 text-sm font-medium"
                      >
                        Track Order
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Orders;
