import { createContext, useContext, useState, useEffect } from 'react';
import { getCart, addToCart, updateCartItem, removeCartItem, clearCart, calculateCartTotal } from '../lib/utils';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cart, setCart] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);
  
  useEffect(() => {
    // Initialize cart from local storage
    const storedCart = getCart();
    setCart(storedCart);
    setCartTotal(calculateCartTotal());
  }, []);
  
  const updateCart = (newCart) => {
    setCart(newCart);
    setCartTotal(calculateCartTotal());
  };
  
  const addItem = (product, quantity) => {
    const updatedCart = addToCart(product, quantity);
    updateCart(updatedCart);
  };
  
  const updateItem = (productId, quantity) => {
    if (updateCartItem(productId, quantity)) {
      const storedCart = getCart();
      updateCart(storedCart);
    }
  };
  
  const removeItem = (productId) => {
    const updatedCart = removeCartItem(productId);
    updateCart(updatedCart);
  };
  
  const clearCartItems = () => {
    clearCart();
    updateCart([]);
  };
  
  return (
    <CartContext.Provider 
      value={{ 
        cart, 
        cartTotal, 
        addItem, 
        updateItem, 
        removeItem, 
        clearCartItems 
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export const useCart = () => useContext(CartContext);
