import useFetch from "../hooks/useFetch";

const Home = () => {
  const { data: products, loading } = useFetch("https://fakestoreapi.com/products");

  return (
    <div className="text-center p-10">
      <div className="flex flex-col md:flex-row items-center justify-between">
        <div className="md:w-1/2 text-left">
          <h1 className="text-3xl font-bold mb-4">Selamat Datang di Toko Online</h1>
          <p className="text-lg">Tempat terbaik untuk menemukan barang unggulan dengan harga terbaik.</p>
        </div>
        <div className="md:w-1/2 flex justify-center">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <img src={products[0]?.image} alt="Produk Unggulan" className="grid grid-cols-2 md:grid-cols-4 gap-4rounded-lg shadow-lg w-40 h-40 object-cover" />
          )}
        </div>
      </div>

      <h2 className="text-2xl font-semibold mt-8">Produk Unggulan</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {products.slice(0, 4).map((product) => (
            <div key={product.id} className="border p-4 rounded shadow-lg">
              <img src={product.image} alt={product.title} className="h-32 mx-auto" />
              <h3 className="text-lg font-bold">{product.title}</h3>
              <p className="text-green-600 font-semibold">${product.price}</p>
            </div>
          ))}
        </div>
      )}

      <div className="mt-8 text-center">
        <h2 className="text-2xl font-semibold">Pemilik Toko</h2>
        <img
          src="https://source.unsplash.com/200x200/?businessman"
          alt="Pemilik Toko"
          className="rounded-full mx-auto mb-4 shadow-lg"
        />
        <p className="text-lg">Saya adalah pemilik toko ini, dengan pengalaman bertahun-tahun di bidang e-commerce.</p>
      </div>
    </div>
  );
};
export default Home;
