// Simple Router Component for Demo
const SimpleRouter = () => {
  const [currentRoute, setCurrentRoute] = useState('/admin');
  
  // Override navigate function for demo
  window.navigate = setCurrentRoute;

  const renderRoute = () => {
    switch (currentRoute) {
      case '/adminDashboard':
        return <AdminDashboard />;
      case '/admin':
      default:
        return <AdminLogin />;
    }
  };

  return renderRoute();
};

// Main App Component
export default function App() {
  return (
    <AuthProvider>
      <SimpleRouter />
    </AuthProvider>
  );
}
