const About = () => {
    return (
      <div className="p-10 text-center">
        <h1 className="text-3xl font-bold mb-4">Tentang Toko Kami</h1>
        <p className="text-lg">Kami adalah toko online terpercaya yang menyediakan berbagai produk berkualitas dengan harga terbaik.</p>
        <img
          src="https://source.unsplash.com/400x300/?store"
          alt="Tentang Kami"
          className="rounded-lg mx-auto mt-6 shadow-lg"
        />
      </div>
    );
  };
  export default About;
