import { useState, useEffect } from "react";

const Orders = () => {
  const [orders, setOrders] = useState([]);

  // Order status configuration
  const orderStatuses = {
    'pending': { 
      label: 'Menunggu Konfirmasi', 
      color: 'bg-yellow-100 text-yellow-800',
      icon: 'â³'
    },
    'confirmed': { 
      label: 'Dikonfirmasi', 
      color: 'bg-blue-100 text-blue-800',
      icon: 'âœ…'
    },
    'preparing': { 
      label: 'Sedang Dipersiapkan', 
      color: 'bg-orange-100 text-orange-800',
      icon: 'ðŸ‘¨â€ðŸ³'
    },
    'shipping': { 
      label: 'Sedang Diantar', 
      color: 'bg-purple-100 text-purple-800',
      icon: 'ðŸšš'
    },
    'delivered': { 
      label: 'Sudah Sampai', 
      color: 'bg-green-100 text-green-800',
      icon: 'ðŸ“¦'
    },
    'completed': { 
      label: 'Selesai', 
      color: 'bg-green-100 text-green-800',
      icon: 'âœ¨'
    },
    'cancelled': { 
      label: 'Dibatalkan', 
      color: 'bg-red-100 text-red-800',
      icon: 'âŒ'
    }
  };

  useEffect(() => {
    // In a real app, you'd fetch from an API
    // For demo purposes, we'll create some sample orders with different statuses
    const sampleOrders = [
      {
        orderId: 'ORD-001',
        orderDate: new Date().toISOString(),
        status: 'shipping',
        customerInfo: {
          fullName: 'John Doe',
          email: 'john@example.com',
          address: 'Jl. Sudirman No. 123, Jakarta',
          phoneNumber: '+62 812-3456-7890'
        },
        items: [
          {
            title: 'iPhone 14 Pro',
            price: 15000000,
            quantity: 1,
            image: 'https://via.placeholder.com/150/000000/FFFFFF?text=iPhone'
          }
        ],
        subtotal: '15,000,000',
        total: '15,000,000'
      },
      {
        orderId: 'ORD-002',
        orderDate: new Date(Date.now() - 86400000).toISOString(),
        status: 'preparing',
        customerInfo: {
          fullName: 'Jane Smith',
          email: 'jane@example.com',
          address: 'Jl. Thamrin No. 456, Jakarta',
          phoneNumber: '+62 813-7890-1234'
        },
        items: [
          {
            title: 'MacBook Air M2',
            price: 18000000,
            quantity: 1,
            image: 'https://via.placeholder.com/150/000000/FFFFFF?text=MacBook'
          }
        ],
        subtotal: '18,000,000',
        total: '18,000,000'
      },
      {
        orderId: 'ORD-003',
        orderDate: new Date(Date.now() - 172800000).toISOString(),
        status: 'delivered',
        customerInfo: {
          fullName: 'Bob Johnson',
          email: 'bob@example.com',
          address: 'Jl. Gatot Subroto No. 789, Jakarta',
          phoneNumber: '+62 814-5678-9012'
        },
        items: [
          {
            title: 'Samsung Galaxy S23',
            price: 12000000,
            quantity: 1,
            image: 'https://via.placeholder.com/150/000000/FFFFFF?text=Samsung'
          }
        ],
        subtotal: '12,000,000',
        total: '12,000,000'
      }
    ];

    // Try to get orders from localStorage, fallback to sample data
    const storedOrders = JSON.parse(localStorage.getItem("orders") || "[]");
    const ordersToShow = storedOrders.length > 0 ? storedOrders : sampleOrders;
    
    // Add default status if not present
    const ordersWithStatus = ordersToShow.map(order => ({
      ...order,
      status: order.status || 'confirmed'
    }));
    
    setOrders(ordersWithStatus.reverse()); // Show latest orders first
  }, []);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('id-ID', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const updateOrderStatus = (orderIndex, newStatus) => {
    const updatedOrders = [...orders];
    updatedOrders[orderIndex].status = newStatus;
    setOrders(updatedOrders);
    
    // Update localStorage
    localStorage.setItem("orders", JSON.stringify(updatedOrders.reverse()));
  };

  const getStatusProgress = (status) => {
    const statusOrder = ['pending', 'confirmed', 'preparing', 'shipping', 'delivered', 'completed'];
    return statusOrder.indexOf(status);
  };

  const StatusProgress = ({ currentStatus }) => {
    const steps = [
      { key: 'confirmed', label: 'Dikonfirmasi' },
      { key: 'preparing', label: 'Dipersiapkan' },
      { key: 'shipping', label: 'Diantar' },
      { key: 'delivered', label: 'Sampai' },
      { key: 'completed', label: 'Selesai' }
    ];

    const currentIndex = getStatusProgress(currentStatus);

    return (
      <div className="mt-4">
        <div className="flex items-center justify-between">
          {steps.map((step, index) => {
            const isActive = index <= currentIndex - 1; // -1 because we start from 'confirmed'
            const isCurrent = step.key === currentStatus;
            
            return (
              <div key={step.key} className="flex flex-col items-center flex-1">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-xs font-semibold ${
                  isActive || isCurrent 
                    ? 'bg-green-500 text-white' 
                    : 'bg-gray-200 text-gray-400'
                }`}>
                  {index + 1}
                </div>
                <span className={`text-xs mt-1 text-center ${
                  isActive || isCurrent ? 'text-green-600 font-medium' : 'text-gray-400'
                }`}>
                  {step.label}
                </span>
                {index < steps.length - 1 && (
                  <div className={`absolute h-0.5 w-full mt-4 ${
                    isActive ? 'bg-green-500' : 'bg-gray-200'
                  }`} style={{ 
                    left: `${(100 / steps.length) * (index + 0.5)}%`,
                    width: `${100 / steps.length}%`,
                    transform: 'translateX(-50%)'
                  }} />
                )}
              </div>
            );
          })}
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-8">Pesanan Anda</h1>
        
        {orders.length === 0 ? (
          <div className="text-center py-12">
            <div className="bg-white rounded-lg shadow-lg p-8">
              <svg className="mx-auto h-24 w-24 text-gray-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M16 11V7a4 4 0 00-8 0v4M5 9h14l1 12H4L5 9z" />
              </svg>
              <h3 className="mt-4 text-lg font-medium text-gray-900">Belum ada pesanan</h3>
              <p className="mt-2 text-gray-500">Anda belum memiliki pesanan. Mulai berbelanja untuk melihat pesanan di sini!</p>
            </div>
          </div>
        ) : (
          <div className="space-y-6">
            {orders.map((order, index) => {
              const statusInfo = orderStatuses[order.status] || orderStatuses['confirmed'];
              
              return (
                <div key={index} className="bg-white rounded-lg shadow-lg overflow-hidden">
                  {/* Order Header */}
                  <div className="bg-gray-50 px-6 py-4 border-b border-gray-200">
                    <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between">
                      <div className="flex flex-col sm:flex-row sm:items-center sm:space-x-6">
                        <div>
                          <h3 className="text-lg font-semibold text-gray-900">
                            Pesanan #{order.orderId}
                          </h3>
                          <p className="text-sm text-gray-600">
                            Dipesan pada {formatDate(order.orderDate)}
                          </p>
                        </div>
                      </div>
                      <div className="mt-4 sm:mt-0 flex items-center space-x-3">
                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${statusInfo.color}`}>
                          <span className="mr-1">{statusInfo.icon}</span>
                          {statusInfo.label}
                        </span>
                        {/* Status Update Dropdown */}
                        <select 
                          value={order.status}
                          onChange={(e) => updateOrderStatus(index, e.target.value)}
                          className="text-sm border border-gray-300 rounded-md px-2 py-1 bg-white"
                        >
                          {Object.entries(orderStatuses).map(([key, status]) => (
                            <option key={key} value={key}>{status.label}</option>
                          ))}
                        </select>
                      </div>
                    </div>
                  </div>

                  {/* Order Progress */}
                  {order.status !== 'cancelled' && (
                    <div className="px-6 py-4 bg-blue-50 border-b border-gray-200">
                      <h4 className="text-sm font-semibold text-gray-900 mb-2">Status Pesanan</h4>
                      <div className="relative">
                        <StatusProgress currentStatus={order.status} />
                      </div>
                    </div>
                  )}

                  {/* Customer Information */}
                  <div className="px-6 pt-4 pb-2">
                    <h4 className="text-sm font-semibold text-gray-900 mb-2">Informasi Pelanggan</h4>
                    <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm text-gray-600">
                      <div>
                        <span className="font-medium">Nama:</span> {order.customerInfo.fullName}
                      </div>
                      <div>
                        <span className="font-medium">Email:</span> {order.customerInfo.email}
                      </div>
                      <div className="sm:col-span-2">
                        <span className="font-medium">Alamat:</span> {order.customerInfo.address}
                      </div>
                      <div>
                        <span className="font-medium">Telepon:</span> {order.customerInfo.phoneNumber}
                      </div>
                    </div>
                  </div>

                  {/* Order Items */}
                  <div className="px-6 py-4">
                    <h4 className="text-sm font-semibold text-gray-900 mb-4">Item Pesanan</h4>
                    <div className="space-y-4">
                      {order.items.map((item, itemIndex) => (
                        <div key={itemIndex} className="flex items-center space-x-4 p-4 bg-gray-50 rounded-lg">
                          <img 
                            src={item.image} 
                            alt={item.title} 
                            className="h-16 w-16 object-contain rounded border bg-white"
                          />
                          <div className="flex-1 min-w-0">
                            <h5 className="text-sm font-medium text-gray-900 line-clamp-2">
                              {item.title}
                            </h5>
                            <p className="text-sm text-gray-600">
                              Jumlah: {item.quantity} Ã— Rp {item.price?.toLocaleString('id-ID') || item.price}
                            </p>
                          </div>
                          <div className="text-sm font-semibold text-gray-900">
                            Rp {((item.price || 0) * (item.quantity || 1)).toLocaleString('id-ID')}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Order Summary */}
                  <div className="px-6 py-4 bg-gray-50 border-t border-gray-200">
                    <div className="flex justify-between items-center">
                      <div className="space-y-1">
                        <div className="flex justify-between text-sm text-gray-600">
                          <span>Subtotal:</span>
                          <span>Rp {order.subtotal}</span>
                        </div>
                        <div className="flex justify-between text-sm text-gray-600">
                          <span>Ongkir:</span>
                          <span className="text-green-600">Gratis</span>
                        </div>
                      </div>
                      <div className="text-right">
                        <p className="text-lg font-bold text-gray-900">
                          Total: <span className="text-purple-600">Rp {order.total}</span>
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default Orders;
