import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get(`https://fakestoreapi.com/products/${id}`)
      .then((response) => setProduct(response.data));
  }, [id]);

  const addToCart = () => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    storedCart.push(product);
    localStorage.setItem("cart", JSON.stringify(storedCart));
    alert("Produk ditambahkan ke keranjang!");
  };

  if (!product) return <p className="text-center text-xl font-semibold">Loading...</p>;

  return (
    <div className="container mx-auto p-10 flex flex-col md:flex-row items-center gap-8">
      <img src={product.image} alt={product.title} className="w-80 h-80 object-contain shadow-lg rounded-lg" />
      <div className="max-w-lg">
        <h1 className="text-3xl font-bold mb-4">{product.title}</h1>
        <p className="text-gray-600 mb-4">{product.description}</p>
        <p className="text-xl font-semibold text-blue-600 mb-4">${product.price}</p>
        <button 
          onClick={addToCart} 
          className="bg-blue-500 text-white py-2 px-4 rounded-lg shadow-md hover:bg-blue-600 transition-all">
          Tambah ke Keranjang
        </button>
      </div>
    </div>
  );
};

export default ProductDetail;
